import React from "react";
import "../App.css";
import "bootstrap/dist/css/bootstrap.min.css";
import Filter from "../Components/Filters";
import { Container, Row, Col } from "react-bootstrap";
import avatar from "../assets/avatar.png";
import Cards from "../Components/Cards";
// import { ReactComponent as searchIcon } from "../assets/icons/search.png";

function Proposal() {
  return (
    <>
      <style type="text/css">
        {`

            .rowStyle {
                margin: 30px 0px 0px 30px;
            }

            .inputCol {
                width: 250px
            }

            .sb-example-3 .search__input {
                width: 100%;
                padding: 10px;
        
                background-color: #FFFFFF;
                transition: transform 250ms ease-in-out;
                font-size: 14px;
                line-height: 18px;
        
                color: #9A99B8;
            //   background-image: url("data:image/svg+xml;charset=utf8,%3Csvg xmlns='http://www.w3.org/2000/svg' width='24' height='24' viewBox='0 0 24 24'%3E%3Cpath d='M15.5 14h-.79l-.28-.27C15.41 12.59 16 11.11 16 9.5 16 5.91 13.09 3 9.5 3S3 5.91 3 9.5 5.91 16 9.5 16c1.61 0 3.09-.59 4.23-1.57l.27.28v.79l5 4.99L20.49 19l-4.99-5zm-6 0C7.01 14 5 11.99 5 9.5S7.01 5 9.5 5 14 7.01 14 9.5 11.99 14 9.5 14z'/%3E%3Cpath d='M0 0h24v24H0z' fill='none'/%3E%3C/svg%3E");
            background-image: url("../assets/icons/search.png");
            background-repeat: no-repeat;
                background-size: 16,53px 16px;
                background-position: 5% center;
                border-radius: 13px;
                border: 0px;
                // transition: all 250ms ease-in-out;
                // backface-visibility: hidden;
                // transform-style: preserve-3d;
            }
        
        .sb-example-3 .search__input::placeholder {
                    color: rgba(87, 87, 86, 0.8);
                    text-transform: uppercase;
                    letter-spacing: 1.5px;
                }

            .colFilter {
                padding: 30px 0px 25px;
                border-bottom: 0.5px solid #D9C4B7;
                marfin-right: 30px
            }

    

            @media screen and (max-width: 767px) {
                .rowStyle {
                    margin: 20px 16px 0px 16px;
                    padding-bottom: 18px;
                    display: flex;
                    justify-content: center;
                }
                
            }

            @media screen and (max-width: 576px) {
              .inputCol {
                 width: 100%;
                 margin-top: 16px;
             }

             .colFilter {
                padding: 0px 0px 16px;
                border-bottom: 0.5px solid #D9C4B7;
            }

            }

          `}
      </style>

      <Container fluid className="proposal p-0">
        <Row className="rowStyle p-0">
          <Col xxl={8} xl={7} lg={6} md={7} sm={6} className="p-0 ">
            <p className="pr"> Предложения </p>
          </Col>
          <Col xl={4} lg={3} md={4} xs={{ span: 12, order: 3 }} sm={{  order: 1 }}className="p-0 inputCol">
            {/* <input
              placeholder="Поиск"
              style={{ width: "100%" }}
              className="searchField"
            ></input> */}
            {/* <div class="sb-example-3">
                <div class="search__container">
                 <input class="search__input" type="text" placeholder="Search"/>
                </div>
            </div> */}
          </Col>
          <Col  lg={1}  xs={{ span: 12, order: 2 }} sm={{  order: 2 }} style={{ paddingLeft: "16px" }}>
            <img className="avatar" src={avatar} alt={"avatar"}></img>
          </Col>
            <Col xs={{ span: 12, order: 1 }} sm={{  order: 3 }} className="colFilter">
              <Filter />
            </Col>
        </Row>
        <Row xs={1} md={1} className="rowStyle">
          <Cards />
        </Row>
      </Container>
    </>
  );
}

export default Proposal;
