import React, { useState } from "react";
import "../App.css";
import { SidebarData } from "./SidebarData";
import { ReactComponent as UnionIcon } from "../assets/icons/Union.svg";
import { ReactComponent as CloseIcon } from "../assets/icons/close.svg";
import { IconContext } from "react-icons";
import { Link } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import { Container, Row, Col, Tab, Navbar } from "react-bootstrap";
// import { ReactComponent as Logo } from "../assets/icons/Logo_colour.svg";
import logo from "../assets/icons/Logo_colour.svg";
import logoName from "../assets/icons/Vector.svg";
import avatar from "../assets/avatar.png";

function Sidebar() {
  const [sidebar, setSidebar] = useState(false);

  const showSidebar = () => setSidebar(!sidebar);

  return (
    <>
      <style type="text/css">{`  
      
      .navbar {
        height: 106px;
        display: none;
        background-color: transparent;
      }

      @media screen and (max-width: 991px) {
        .navbar {

        display: flex;
        }
      }


      
      `}</style>
      <Navbar expand="lg" variant="light">
        <Container>
          <Navbar.Brand href="#">Navbar</Navbar.Brand>
        </Container>
      </Navbar>

      {/* <Row className="logoHoriz nowrap">
        <Col className="menu-bars">
          <Link to="#">
            <UnionIcon className="nav_icon" onClick={showSidebar} />
          </Link>
        </Col>
        <Col className="logo_colour">
          <img src={logo} alt={"logo"} />
        </Col>
        <Col className="name p-0">
          <img src={logoName} alt={"Company name"} />
        </Col>
        <Col className="imgNav">
          <img className="avatarNav" src={avatar} alt={"avatar"}></img>
        </Col>
      </Row> */}
      <div className="sidebarLogo">
        <Row className="logoVertical">
          <Col md={3} className="logo_colour">
            <img src={logo} alt={"logo"} />
          </Col>
          <Col md={9} className="name p-0">
            <img src={logoName} alt={"Company name"} />
          </Col>
        </Row>
      </div>
      <div className="Sidebar">
        <ul className="SidebarList">
          {SidebarData.map((val, key) => {
            return (
              <li
                key={key}
                className="row"
                id={window.location.pathname == val.path ? "active" : ""}
                onClick={() => {
                  window.location.pathname = val.path;
                }}
              >
                <div
                  id={
                    window.location.pathname == val.path ? "iconActive" : "icon"
                  }
                >
                  {val.icon}
                </div>
                <div id="title">{val.title}</div>
              </li>
            );
          })}
        </ul>
      </div>
      {/* </nav> */}
    </>
  );
}

export default Sidebar;
