import React, { Component } from "react";
import "../App.css";
import "bootstrap/dist/css/bootstrap.min.css";
import { ListItemCardData } from "./ListItemCardData";
import { CardData } from "./CardData";
import { ReactComponent as GiftIcon } from "../assets/icons/gift.svg";
import { ReactComponent as NextIcon } from "../assets/icons/next.svg";

import {
  Container,
  Row,
  Col,
  Card,
  ListGroup,
  ListGroupItem,
} from "react-bootstrap";

// function Cards() {}

class Cards extends Component {
  render() {
    return (
      <>
        <style type="text/css">
          {`
     .col {
      width: auto;

      }

     .colCard {
       padding-right: 43px;
       padding-left: 0;
      }

    .card {
      border-radius: 4px 20px 20px 20px;
      border: 0px;
      box-shadow: 0px 10px 20px rgba(233, 216, 207, 0.3);
      width: 343px;
      height: 548px;

    }
    .card>.list-group:last-child {
       border-radius: 20px
    }

    .card-header  {
        color: #272931;
         background-color: #ffffff;
         height: 60px;
         border: 0px;
         justify-content: center;
         align-items: center;
         padding-top:14px;
         padding-bottom: 10px;
      }

      .card-header:first-child {
        border-radius:  20px;
      }

      .card-img, .card-img-bottom, .card-img-top  {
        width: 315px;
        height: 160px;
        margin: 0px 14px;
      }

      .card-img, .card-img-top {
        background: #C4C4C4;
        border-radius: 4px 20px 20px 20px;
        border: 0px;
      }

      .iconHeader {
        border-radius: 11px;
      }

      .header {
        flex-direction: row;
        display: flex;
        justify-content: center;
        align-items: center;
      }

      .titleHeader {
        font-weight: bold;
        font-size: 14px;
        line-height: 16px;
        text-align: center;
        flex: 30%;
        margin-left: 11px;
        display: grid;
        justify-content: start;
      }


      .status{
        background: rgba(0, 198, 103, 0.08);
        border-radius: 8px;
        font-weight: 600;
        font-size: 13px;
        width: 90px;
        height: 30px;
        justify-content: center;
        align-items: center;
        color: #00C667;
        display: grid;
      }

      .card-body{
        padding: 20px 14px 14px 20px
      }

      .card-title {
        font-style: normal;
        font-weight: bold;
        font-size: 16px;
        line-height: 19px;
        margin-bottom: 10px;
      }

      .card-text{
        display: flex;
        flex-direction: row;
        justify-content: start;
        align-items: flex-start;
        height: 32px;
        margin-bottom: 14px
      }

      .giftIcon {
        display: grid;
        width: 17px;
        height:18px;
        flex: 10%;
        margin-top: 5px;
        margin-right: 7px;
      }

      .giftTitle {
        font-style: normal;
        font-weight: bold;
        font-size: 14px;
        line-height: 16px;
        color: #FC5A8B;
        margin-right: 13px;
      }

      .list-group-flush>.list-group-item {
        padding: 10px 0px;
        height: 50px;
        justify-content: start;
        align-items: center;
        display: flex;

      }

      .listTitle {
        flex:5%;
        font-weight: 600;
        font-size: 14px;
        line-height: 16px;
      }

      .listItem1{
        background: #F93C47;
        border-radius: 50px;
        width: 30px;
        height:30px;
        color: #FFFFFF;
        font-weight: bold;
        font-size: 14px;
        line-height: 16px;

        justify-content: center;
        align-items: center;
        display: grid;
        margin-right:12px
        
      }

      .listItem2{
        background: #F7F7FA;
        border-radius: 50px;
        width: 30px;
        height:30px;
        color: #9A99B8;
        font-style: normal;
        font-weight: bold;
        font-size: 14px;
        line-height: 16px;

        justify-content: center;
        align-items: center;
        display: grid;
        margin-right:12px
      }

      .listItem3{

      }

      .listItem4 {

      }





    `}
        </style>
        {/* <Row xs={1} md={2}> */}
        {CardData.map((item, key) => {
          return (
            <Col className="colCard">
              <Card key={key}>
                <Card.Header className="bg">
                  <div className="header">
                    {item.iconHeader}
                    <span className="titleHeader">{item.titleHeader}</span>
                    <span className="status">{item.status}</span>
                  </div>
                </Card.Header>
                <Card.Img
                  variant="top"
                  src={require(`../assets/${item.image}.png`)}
                />
                <Card.Body>
                  <Card.Title>{item.title}</Card.Title>
                  <Card.Text>
                    <GiftIcon className="giftIcon" />
                    <span className="giftTitle">{item.giftTitle}</span>
                  </Card.Text>

                  {ListItemCardData.map((val, key) => {
                    return (
                      <ListGroup key={key} className="list-group-flush">
                        <ListGroupItem className="border-top">
                          <span className="listTitle">{val.item1}</span>
                          <span className="listItem1">{item.item1}</span>
                          <NextIcon />
                        </ListGroupItem>
                        <ListGroupItem>
                          <span className="listTitle">{val.item2}</span>
                          <span className="listItem2">{item.item2}</span>
                          <NextIcon />
                        </ListGroupItem>
                        <ListGroupItem>
                          <span className="listTitle">{val.item3}</span>
                          <span className="listItem3">{item.item3}</span>
                        </ListGroupItem>
                        <ListGroupItem>
                          <span className="listTitle">{val.item4}</span>
                          <span className="listItem4">{item.item4}</span>
                        </ListGroupItem>
                      </ListGroup>
                    );
                  })}
                </Card.Body>
              </Card>
            </Col>
          );
        })}
        {/* </Row> */}
      </>
    );
  }
}

export default Cards;
